25;  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    ms_StringBuilder.Append(m_Name);  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (m_Count &amp;lt;= &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;)  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;return&lt;/span&gt;;  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;// 输出统计结果  &lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    ms_StringBuilder.Append(&lt;span class=&#34;string&#34;&gt;&amp;quot; [&amp;quot;&lt;/span&gt;);  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    ms_StringBuilder.Append(&lt;span class=&#34;string&#34;&gt;&amp;quot;Count: &amp;quot;&lt;/span&gt;);  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    ms_StringBuilder.Append(m_Count);  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    ms_StringBuilder.Append(&lt;span class=&#34;string&#34;&gt;&amp;quot;, Time: &amp;quot;&lt;/span&gt;);  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;// 转换 tick 为毫秒、秒、分钟  &lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;built_in&#34;&gt;float&lt;/span&gt; ms = (&lt;span class=&#34;built_in&#34;&gt;float&lt;/span&gt;)m_Time / TimeSpan.TicksPerMillisecond;  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;built_in&#34;&gt;float&lt;/span&gt; sec = (&lt;span class=&#34;built_in&#34;&gt;float&lt;/span&gt;)m_Time / TimeSpan.TicksPerSecond;  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;built_in&#34;&gt;float&lt;/span&gt; min = (&lt;span class=&#34;built_in&#34;&gt;